string xmlFile = "User.xml";
oleDbUnitTest.ReadXmlSchema(xmlSchemaFile);
oleDbUnitTest.ReadXml(xmlFile);
oleDbUnitTest.PerformDbOperation(DbOperation.CleanInsertIdentity);
